	{
        using var context = DbContextFactory.Create();

        foreach (var entity in entities)
        {
            MarkToUpdate(context, entity);

			if (withAudit)
				entity.WriteAuditInfo();
        }

		int count = context.SaveChanges();

		if (count > 0)
			OnSaved(entities, LogType.Update);

