	/// 기본키가 일치하는 엔터티를 삭제한다.
	/// </summary>
	/// <param name="playerId">기본키</param> 
	/// <returns>삭제된 엔터티의 갯수</returns>
    public virtual int DeleteByKey(int playerId )
	{
        using var context = DbContextFactory.Create();

		var query = from x in context.Set<Player>()
		where x.PlayerId == playerId 
		select x;

		var entity = query.FirstOrDefault();

        if (entity == null)
            return 0;

		MarkToDelete(context, entity);

		return context.SaveChanges();
	}

    public virtual Task<int> DeleteByKeyAsync(int playerId )
	=> Task.Run(() => DeleteByKey(playerId ));

    /// <summary>
    /// 기본키가 일치하는 엔터티가 없으면 삽입한다.
    /// </summary>
    /// <param name="entity">삽입할 엔터티</param>
	/// <returns>삽입된 엔터티</returns>
    public virtual Player InsertIfNotExist(Player entity)
	{
        using var context = DbContextFactory.Create();

	    if (context.Set<Player>().Count(x =>  x.PlayerId == entity.PlayerId ) > 0)
	        return null;

	    return Insert(entity);
	}

    public virtual Task<Player> InsertIfNotExistAsync(Player entity)
	=> Task.Run(() => InsertIfNotExist(entity));

    /// <summary>
    /// 기본키가 일치하는 엔터티가 있으면 갱신하고 없으면 삽입한다.
    /// </summary>
    /// <param name="entity">삽입 혹은 갱신할 엔터티</param>
	/// <param name="withAudit">감사정보(수정자, 수정일)를 기록한다.</param>
	/// <returns>삽입했으면 true, 갱신했으면 false</returns>
    public virtual bool InsertOrUpdate(Player entity, bool withAudit = false)
	{
        using var context = DbContextFactory.Create();
            
	    if (context.Set<Player>().Count(x =>  x.PlayerId == entity.PlayerId ) > 0)
	    {
	        Update(entity);
            return false;
        }
        else
	    {
	        Insert(entity);
            return true;
        }
	}

    public virtual Task<bool> InsertOrUpdateAsync(Player entity, bool withAudit = false)
	=> Task.Run(() => InsertOrUpdate(entity, withAudit));
	
	
}
